    padding: 0.2rem 0.7rem;
    backdrop-filter: blur(4px);
  }
  .hero-chip-warn {
    background: oklch(0.85 0.16 60 / 0.25);
    border-color: oklch(0.85 0.16 60 / 0.4);
  }
  .hero-graphic {
    position: absolute;
    inset: 0;
    pointer-events: none;
  }
  .hero-orb {
    position: absolute;
    border-radius: 9999px;
    filter: blur(48px);
  }
  .hero-orb-1 {
    width: 14rem;
    height: 14rem;
    right: -2rem;
    top: -3rem;
    background: oklch(0.65 0.22 290 / 0.5);
  }
  .hero-orb-2 {
    width: 10rem;
    height: 10rem;
    right: 8rem;
    bottom: -2rem;
    background: oklch(0.55 0.18 200 / 0.4);
