  .icon-violet { background: oklch(0.52 0.22 300 / 0.12); color: oklch(0.52 0.22 300); }
  .icon-amber  { background: oklch(0.75 0.18 60  / 0.15); color: oklch(0.6  0.2  60); }
  .icon-rose   { background: oklch(0.65 0.2  15  / 0.12); color: oklch(0.55 0.22 15); }

