    gap: 1rem;
  }
  @media (min-width: 1024px) {
    .bottom-grid { grid-template-columns: 1fr 380px; }
  }
  .chart-card, .side-card, .recent-card {
    background: var(--card);
    border: 1px solid var(--border);
    border-radius: 1rem;
    padding: 1.5rem;
    box-shadow: var(--shadow-card);
  }
  .section-header {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    margin-bottom: 1.25rem;
  }
  .section-icon {
    color: var(--primary);
    opacity: 0.8;
  }
  .section-title {
    font-size: 0.875rem;
    font-weight: 700;
    letter-spacing: -0.01em;
    color: var(--foreground);
  }
  .chart-placeholder {
    height: 8rem;
    display: flex;
    align-items: center;
