    color: var(--muted-foreground);
    font-size: 0.85rem;
  }

  /* Upcoming list */
  .upcoming-list {
    display: flex;
    flex-direction: column;
    gap: 0;
  }
  .upcoming-item {
    display: flex;
    align-items: center;
    gap: 0.75rem;
