  }
  .upcoming-info {
    flex: 1;
    display: flex;
    flex-direction: column;
    gap: 0.1rem;
  }
  .upcoming-title {
    font-size: 0.82rem;
    font-weight: 600;
    color: var(--foreground);
    line-height: 1.3;
  }
  .upcoming-date {
    font-size: 0.72rem;
    color: var(--muted-foreground);
  }
  .upcoming-price {
    font-size: 0.78rem;
    font-weight: 700;
    color: oklch(0.46 0.22 264);
    white-space: nowrap;
  }

  /* Recent table */
  .recent-table-wrap {
    overflow-x: auto;
  }
  .recent-table {
    width: 100%;
    border-collapse: collapse;
    font-size: 0.82rem;
  }
  .recent-table thead th {
    text-align: left;
    padding: 0 0.75rem 0.625rem;
    font-size: 0.7rem;
    font-weight: 700;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    color: var(--muted-foreground);
    border-bottom: 1px solid var(--border);
  }
  .recent-table thead th:first-child { padding-left: 0; }
  .recent-table tbody tr {
    border-bottom: 1px solid var(--border);
  }
  .recent-table tbody tr:last-child { border-bottom: none; }
  .recent-table tbody td {
    padding: 0.75rem 0.75rem;
    vertical-align: middle;
    color: var(--foreground);
  }
  .recent-table tbody td:first-child { padding-left: 0; }
  .recent-muted { color: var(--muted-foreground) !important; }

  /* Badges */
  .badge-paid {
    display: inline-flex;
    align-items: center;
    font-size: 0.68rem;
    font-weight: 700;
    letter-spacing: 0.03em;
    border-radius: 9999px;
    padding: 0.15rem 0.55rem;
    background: oklch(0.7 0.16 160 / 0.15);
    color: oklch(0.42 0.14 160);
    border: 1px solid oklch(0.7 0.16 160 / 0.3);
  }
  .badge-unpaid {
    display: inline-flex;
    align-items: center;
    font-size: 0.68rem;
    font-weight: 700;
    letter-spacing: 0.03em;
    border-radius: 9999px;
    padding: 0.15rem 0.55rem;
    background: oklch(0.65 0.2 60 / 0.12);
    color: oklch(0.5 0.2 60);
    border: 1px solid oklch(0.65 0.2 60 / 0.3);
  }
}

